mapper.select:
SELECT TOTAL_POINT
FROM   USER_INFO
WHERE  USER_ID = #{userId}

mapper.update:
UPDATE USER_INFO
SET    TOTAL_POINT = TOTAL_POINT - #{newPoints}
WHERE  USER_ID = #{userId}

mapper.select:
SELECT CURRENT_POINT
FROM   SUPPORT
WHERE  SUPPORT_ID = #{supportId}

mapper.update:
UPDATE SUPPORT
SET    CURRENT_POINT = CURRENT_POINT + #{pointsToAdd}
WHERE  SUPPORT_ID = #{supportId}
